require('dotenv').config();
const nodemailer = require('nodemailer')

const transporter = nodemailer.createTransport({
    server: 'gmail',
    auth: {
        user: "mysik.bazyk@gmail.com",
        pass: "vfrcbv010288",
    }
})

function sendMail(point_of_departure, destination, name, email, weight, phone) {
    const mailOptions = {
        from: process.env.EMAIL,
        to: process.env.EMAIL,
        subject: "Що до розрахунку вартості перевезень",
        message: {
            "from": point_of_departure,
            "to": destination,
            "name": name,
            "email": email,
            "weight": weight,
            "phone": phone,
        }
    }

    transporter.sendMail(mailOptions)
}

export default {
    sendMail
}
