import React, {useRef, useState} from 'react';
import email_js from '@emailjs/browser';

export default function sendEmail(departure, importance, name, phone, weight, email) {
    const message = {
        from_name: name,
        email: email,
        message: {
            departure: departure,
            importance: importance,
            weight: weight,
            phone: phone
        },
    }

    email_js.send("service_fktr0no", "template_q36cnic", message,
        {publicKey: 't262JebAxXrwwSnI3',});
}
