let index = get()-1;
let left = 0
console.log(index);
function scrollLeft() {
    if (index <= get()) {
        left -= 410;
        document.querySelector('.Test').style.marginLeft = left + 'px';
        index++;
        console.log(index);
    }else {
        document.querySelector('.Test').style.marginLeft = 0 + 'px';
    }
}

let indexRight = 0
function scrollRight() {
    if (indexRight > 0) {
        left += 410;
        document.querySelector('.Test').style.marginLeft = left + 'px';
        indexRight--;
    }
}

function get(){
    return document.querySelectorAll('.test_list').length;
}

export default {
    scrollLeft, scrollRight
}
