import React, {useRef} from 'react';
import email_js from '@emailjs/browser';

export default function MailSender() {
    const formMail = useRef();

    const sendEmail = (e) => {
        e.preventDefault()
        email_js.sendForm('service_fktr0no', 'template_5st3tnf',
            formMail.current, {publicKey: 't262JebAxXrwwSnI3',}).then(
            () => {
                console.log('SUCCESS!');
            },
            (error) => {
                console.log('FAILED...', error.text);
            },
        );
    }

    return (
        <>
            <div className="content-us">
                <form ref={formMail} onSubmit={sendEmail}>
                    <label>Name</label>
                    <input type="text" name="user_name"/>
                    <label>Email</label>
                    <input type="email" name="user_email"/>
                    <label>Message</label>
                    <textarea name="message"/>
                    <input type="submit" value="Send"/>
                </form>
            </div>
        </>
    )
}
