import React, {useRef} from 'react';
import email from '@emailjs/browser';

function ContactUs(departure, importance, weight, phone, email, name, classTeg) {
    const form = useRef();

    let param = {
        from_name: departure,
        email: email,
        message: "Клієнт:\nЗвідки: " + name +
            "\nКуда: " + importance +
            "\nВага: " + weight +
            "\nТелефон: " + phone
    }

    function sendMail(param, message) {
        email.send("service_fktr0no", "template_q36cnic", param).then(function () {
            message.innerHTML = "Повідомлення відправлено!!!"

            setTimeout(() => {
                message.innerHTML = ""
            }, 4000)
        })
    }
}


