import React, {useRef, useState} from 'react';
import email_js from '@emailjs/browser';

export default function MailSender() {
    const formMail = useRef();
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');
    const [name, setName] = useState('');

    const sendEmail = () => {
        // e.preventDefault()
        email_js.sendForm('service_fktr0no', 'template_5st3tnf',
            formMail.current, {publicKey: 't262JebAxXrwwSnI3',}).then(
            () => {
                console.log('SUCCESS!');
            },
            (error) => {
                console.log('FAILED...', error.text);
            },
        );
    }

    function sendEmail1() {
        emailjs.send("service_fktr0no","template_q36cnic",{
            from_name: "Maks",
            email: "mysi",
            message: "hello",
        });
    }

    return (
        <>
            <div className="content-us">
                <span className="form">
                    <label>Name</label>
                    <input type="text" name="user_name" onChange={(e) => setName(e.target.value)} />
                    <label>Email</label>
                    <input type="email" name="user_email" onChange={(e) => setEmail(e.target.value)}/>
                    <label>Message</label>
                    <textarea name="message" onChange={(e) => setMessage(e.target.value)}/>
                    <input type="submit" value="Send" onClick={() => sendEmail()} />
                </span>
            </div>
        </>
    )
}
