let index = 0;
let left = 0
function scrollLeft() {
    if (index < get()) {
        left += 410;
        document.querySelector('.Test').style.marginLeft = left + 'px';
        index++;
    }
    if (index === get()) {
        index = 0;
        left = 0;
    }
}


function scrollRight() {
    if (index < get()) {
        left += 410;
        document.querySelector('.Test').style.marginLeft = left + 'px';
        index++;
    }
    if (index === get()) {
        index = 0;
        left = 0;
    }
}

function get(){
    return document.querySelectorAll('.test_list').length;
}

export default {
    scrollLeft, scrollRight
}
