import React, {useRef} from 'react';
import email from '@emailjs/browser';

function ContactUs(departure, importance, weight, phone, email, name, classTeg) {
    const form = useRef();

    let param = {
        from_name: departure,
        email: email,
        message: "Клієнт:\nЗвідки: " + name +
            "\nКуда: " + importance +
            "\nВага: " + weight +
            "\nТелефон: " + phone
    }
    let message = document.querySelector(classTeg)
    sendEmail(message)
}

const sendEmail = (e) => {
    e.preventDefault();

    emailjs
        .sendForm('service_fktr0no',
            'template_5st3tnf', form.current, {
            publicKey: 't262JebAxXrwwSnI3',
        })
        .then(
            () => {
                console.log('SUCCESS!');
            },
            (error) => {
                console.log('FAILED...', error.text);
            },
        );
};

export default ContactUs


