import "../../../src/css/header.css"

const Navigation = () => {
    let flag = true;

    const nav_value = {
        orderCall: "Замовлені дзвінки"
    }

    function toggleNav() {
        let order = document.querySelector(".order");
        if (flag) {
            flag = false;
            order.style.display = 'flex';
        } else {
            flag = true;
            order.style.display = 'none';
        }
    }

    return (
        <>
            <div className="navigation">
                <button onClick={() => toggleNav()}>{nav_value.orderCall}</button>
                {/*<button></button>*/}
                {/*<button></button>*/}
                {/*<button></button>*/}
                {/*<button></button>*/}
                {/*<button></button>*/}
                {/*<button></button>*/}
            </div>
        </>
    )
}

export default Navigation;
