import React, {useRef} from 'react';
import emailjs from '@emailjs/browser';

export default function ContactUs(departure, importance, weight, phone, email, name, classTeg) {
    const formMail = useRef();

    let param = {
        from_name: departure,
        email: email,
        message: "Клієнт:\nЗвідки: " + name +
            "\nКуда: " + importance +
            "\nВага: " + weight +
            "\nТелефон: " + phone
    }

    const sendEmail = (e) => {
        e.preventDefault()
        emailjs
            .sendForm('service_fktr0no',
                'template_5st3tnf',
                formMail.current, {
                    publicKey: 't262JebAxXrwwSnI3',
                })
            .then(
                () => {
                    console.log('SUCCESS!');
                },
                (error) => {
                    console.log('FAILED...', error.text);
                },
            );

        return (
            <form ref={formMail} onSubmit={sendEmail}>
                <label>Name</label>
                <input type="text" name="user_name"/>
                <label>Email</label>
                <input type="email" name="user_email"/>
                <label>Message</label>
                <textarea name="message"/>
                <input type="submit" value="Send"/>
            </form>
        );
    };
}
