import './send-review.css'
import {useRef, useState} from "react";

export default function SendReview() {
    let response = false;
    let messageOne = "Message sender"
    let errorOne = "Fill in all fields"

    const [departure, setDeparture] = useState(null)
    const [arrival, setArrival] = useState(null)
    const [enterprise, setEnterprise] = useState(null)
    const [description, setDescription] = useState(null)
    const [error, setError] = useState(null)

    const clearInput = useRef(null)

    function mess(message) {
        setError(message)
        setTimeout(() => {
            setError("")
        }, 3000)
    }

    function clear() {

        console.log(clearInput.current);
    }


    function sendReview() {
        if (departure == null || departure === "" &&
            arrival == null || arrival === "" &&
            enterprise == null || enterprise === null &&
            description === null || description === null) {
            response = true;
        } else {
            fetch("/reviews/", {
                method: "POST",
                body: JSON.stringify({
                    point: {
                        departure: departure,
                        arrival: arrival
                    },
                    enterprise: enterprise,
                    description: description,
                }),
                headers: {
                    "Content-Type": "application/json", charset: "UTF-8"
                }
            })
                .then(res => res.json()).then((res) => {
                console.log(res)
                response = false;
            })
        }
    }

    return (
        <>
            <div className="row">
                <div className="col-12">
                    <input ref={clearInput} type="text"
                           placeholder="Пункт відправки"
                           onChange={event => setDeparture(event.target.value)}/>
                    <input ref={clearInput} type="text" placeholder="Пункт призначення"
                           onChange={event => setArrival(event.target.value)}/>
                    <input ref={clearInput} type="text" placeholder="Підприємство"
                           onChange={event => setEnterprise(event.target.value)}/>
                    <textarea ref={clearInput} placeholder="Опис" rows="20"
                              onChange={event => setDescription(event.target.value)}/>
                    <button
                        onClick={() => {
                            sendReview()
                            !response ? mess(messageOne) : mess(errorOne)
                            clear()
                        }}
                        type="submit"
                        className="button">Залишити відгук
                    </button>
                </div>
                <div className="message">
                    <p>{error}</p>
                </div>
            </div>
        </>
    )
}
