export default function sendTop(departure, importance, weight, email, name, phone) {
    let param = {
        from_name: departure,
        email: email,
        message: "Клієнт:\nЗвідки: " + name +
            "\nКуда: " + importance +
            "\nВага: " + weight +
            "\nТелефон: " + phone
    }
    let message = document.querySelector('.messageTop')
    sendMail(param, message)
}

function sendMail(param, message) {
    emailjs.send("service_fktr0no", "template_q36cnic", param).then(function () {
        message.innerHTML = "Повідомлення відправлено!!!"

        setTimeout(() => {
            message.innerHTML = ""
        }, 4000)
    })
}
