import React, {useRef, useState} from 'react';
import email_js from '@emailjs/browser';

export default function MailSender() {
    const formMail = useRef();
    const [email, setEmail] = useState('');
    const [mess, setMessage] = useState('');
    const [name, setName] = useState('');

   const message = {
       from_name: name,
       email: email,
       message: mess,
   }
    function sendEmail1() {
        email_js.send("service_fktr0no","template_q36cnic", message,
            {publicKey: 't262JebAxXrwwSnI3',});
    }

    return (
        <>
            <div className="content-us">
                <span className="form">
                    <label>Name</label>
                    <input type="text" name="user_name" onChange={(e) => setName(e.target.value)} />
                    <label>Email</label>
                    <input type="email" name="user_email" onChange={(e) => setEmail(e.target.value)}/>
                    <label>Message</label>
                    <textarea name="message" onChange={(e) => setMessage(e.target.value)}/>
                    <input type="submit" value="Send" onClick={() => sendEmail1()} />
                </span>
            </div>
        </>
    )
}
