import './send-review.css'
import {useState} from "react";

export default function SendReview() {
    const [departure, setDeparture] = useState(null)
    const [arrival, setArrival] = useState(null)
    const [enterprise, setEnterprise] = useState(null)
    const [description, setDescription] = useState(null)

    function sendReview() {
        fetch("/reviews/", {
            method: "POST",
            body: JSON.stringify({
                departure: departure,
                arrival: arrival,
                enterprise: enterprise,
                description: description,
            }),
            headers: {
                "Content-Type": "application/json", charset: "UTF-8"
            }
        })
            .then(res => res.json()).then((res) => {
            console.log(res)
        })
    }

    return (
        <>
            <div className="row">
                <div className="col-12">
                    <input type="text" placeholder="Пункт відправки"
                           onChange={event => setDeparture(event.target.value)}/>
                    <input type="text" placeholder="Пункт призначення"
                           onChange={event => setArrival(event.target.value)}/>
                    <input type="text" placeholder="Підприємство"
                           onChange={event => setEnterprise(event.target.value)}/>
                    <textarea placeholder="Опис" rows="20"
                              onChange={event => setDescription(event.target.value)}/>
                    <button
                        onClick={() => sendReview()}
                        type="submit"
                        className="button">Залишити відгук</button>
                </div>
            </div>
        </>
    )
}
