let index = 0;
let left = 0
function scrollLeft() {
    let list = document.querySelectorAll('.test_list');
    if (index < list.length) {
        left += 370;
        document.querySelector('.Test').style.marginLeft = left + 'px';
        index++;
    }
    if (index === list.length) {
        index = 0;
        left = 0;
    }
}


function scrollRight() {
    document.querySelector('.Test').style.marginLeft = -350 + 'px';
}

export default {
    scrollLeft, scrollRight
}
