let index = 3;
let left = 0
function scrollLeft() {
    if (index < get()) {
        left += 410;
        document.querySelector('.Test').style.marginLeft = left + 'px';
        index++;
    }
    if (index === get()) {
        index = 0;
        left = 0;
    }
}

let indexRight = get()
function scrollRight() {
    if (indexRight > 0) {
        left -= 410;
        document.querySelector('.Test').style.marginLeft = left + 'px';
        indexRight--;
    }
    if (indexRight === 0) {
        indexRight = get();
        left = 0;
    }
}

function get(){
    return document.querySelectorAll('.test_list').length;
}

export default {
    scrollLeft, scrollRight
}
